mapper.select:
/* adm1200DAO.selectTotalMemberCnt - 전체 회원 수 조회 코드 */
SELECT COUNT(M_EML) FROM MEMBER M WHERE M_DLT = 0
AND M.M_ST = #{memberState}
AND M.M_NM LIKE CONCAT('%' #{searchValue} '%')

/* adm1200DAO.selectMemberInfo - 회원 정보 조회 코드 */
SELECT SC.SUB_NM AS M_ST ,M_EML ,M_NM ,M_TEL ,M_GND ,M_BTD ,M_JOIN_D ,COUNT(RV.REZ_M_EML) AS totalNum ,SUM(CASE WHEN RV.REZ_STATE = 'CP_TM' THEN MN.MN_PR ELSE 0 END) AS totalPrice ,COUNT(CASE WHEN RV.REZ_STATE = 'CC_REZ' THEN 1 ELSE NULL END) AS reservationCancleNum FROM MEMBER M LEFT JOIN SUB_CD SC ON M.M_ST = SC.SUB_CD LEFT JOIN RESERVATION RV ON M.M_EML = RV.REZ_M_EML LEFT JOIN MENU MN ON RV.REZ_MN_CD = MN.MN_CD WHERE 1=1 AND M_DLT = 0
AND M.M_ST = #{memberState}
AND M.M_NM LIKE CONCAT('%' #{searchValue} '%')
GROUP BY M.M_ST, M.M_JOIN_D, M.M_EML ORDER BY M.M_ST, totalNum DESC, totalPrice DESC
LIMIT 0
LIMIT #{startIndex}
,#{listSize}
